<?php/** * author.class.php *  **/class author extends Table {	public static $DATABASE_NAME = 'library';	public static $TABLE_NAME = 'author';	public static $PRIMARY_KEY = array('id'=>'id');	public static $FIELD_NAME = array('id'=>'id','name'=>'name');	protected $FIELD_MODIFIED = array();	protected $RESULT = array();	protected static $FOREIGN_KEYS = array();	protected $id = null;	protected $name = null;	public function set_id($pArg='0') {		IF ( $this->id !== $pArg){			$this->id=$pArg; $this->FIELD_MODIFIED['id']=1;		}	}	public function set_name($pArg='0') {		IF ( $this->name !== $pArg){			$this->name=$pArg; $this->FIELD_MODIFIED['name']=1;		}	}	public function get_id() { return (integer) $this->id; }	public function get_name() { return (string) $this->name; }}