<?php/** * books.class.php *  **/class books extends Table {	public static $DATABASE_NAME = 'library';	public static $TABLE_NAME = 'books';	public static $PRIMARY_KEY = array('id'=>'id','author'=>'author');	public static $FIELD_NAME = array('id'=>'id','name'=>'name','author'=>'author');	protected $FIELD_MODIFIED = array();	protected $RESULT = array();	protected static $FOREIGN_KEYS = array('author'=>array('TABLE_NAME'=>'author', 'COLUMN_NAME'=>'id', 'DATABASE_NAME'=>'library'));	protected $id = null;	protected $name = null;	protected $author = null;	protected $FK_authorauthor;	public function set_id($pArg='0') {		IF ( $this->id !== $pArg){			$this->id=$pArg; $this->FIELD_MODIFIED['id']=1;		}	}	public function set_name($pArg='0') {		IF ( $this->name !== $pArg){			$this->name=$pArg; $this->FIELD_MODIFIED['name']=1;		}	}	public function set_author($pArg='0') {		IF ( $this->author !== $pArg){			$this->author=$pArg; $this->FIELD_MODIFIED['author']=1;		}	}	protected function set_FK_authorauthor($pArg='0') {$this->FK_authorauthor=$pArg; }	public function get_id() { return (integer) $this->id; }	public function get_name() { return (string) $this->name; }	public function get_author() { return (integer) $this->author; }	public function get_FK_authorauthor($force_get=TRUE) { if ($this->FK_authorauthor!== null || $force_get === FALSE) { return $this->FK_authorauthor; } else {$this->FK_authorauthor = new author();$this->FK_authorauthor->load(array(self::$FOREIGN_KEYS['author']['COLUMN_NAME'] => $this->author));return $this->FK_authorauthor; } }}