<?php/** * users.class.php *  **/class users extends Table {	public static $DATABASE_NAME = 'library';	public static $TABLE_NAME = 'users';	public static $PRIMARY_KEY = array('id'=>'id');	public static $FIELD_NAME = array('id'=>'id','name'=>'name','email'=>'email');	protected $FIELD_MODIFIED = array();	protected $RESULT = array();	protected static $FOREIGN_KEYS = array();	protected $id = null;	protected $name = null;	protected $email = null;	public function set_id($pArg='0') {		IF ( $this->id !== $pArg){			$this->id=$pArg; $this->FIELD_MODIFIED['id']=1;		}	}	public function set_name($pArg='0') {		IF ( $this->name !== $pArg){			$this->name=$pArg; $this->FIELD_MODIFIED['name']=1;		}	}	public function set_email($pArg='0') {		IF ( $this->email !== $pArg){			$this->email=$pArg; $this->FIELD_MODIFIED['email']=1;		}	}	public function get_id() { return (integer) $this->id; }	public function get_name() { return (string) $this->name; }	public function get_email() { return (string) $this->email; }}